import React, { Component } from "react";

import { connect } from "react-redux";
import { FormattedMessage } from "react-intl";

class HomeFooter extends Component {
  componentDidMount() {
    // Kiểm tra xem URL có chứa tham số #scroll-to không
    const urlParams = new URLSearchParams(window.location.hash);
    const scrollTo = urlParams.get("#scroll-to");
    if (scrollTo) {
      // Tùy thuộc vào giá trị của tham số, cuộn đến mục cụ thể trên trang
      const element = document.getElementById(scrollTo);
      if (element) {
        setTimeout(() => {
          element.scrollIntoView({ behavior: "smooth" });
        }, 10000);
      }
    }
  }
  render() {
    return (
      <div className="home-footer">
        <p>
          &copy; 2023 EXOL. More information, please visit my facebook.
          <a target="_blank" href="https://www.facebook.com/weareoneEXO">
            &#8594; Click here &#8592;
          </a>
        </p>
        <p id="element2">
          “Sách mở ra trước mắt ta những chân trời mới”. Thật vậy! Sách có vai
          trò vô cùng quan trọng cho cuộc sống của chúng ta bởi nó chính là
          nguồn cung cấp tri thức chính về tất cả các ngành khoa học, giúp ta mở
          mang vốn hiểu biết, từ đó đạt được thành công và cống hiến cho sự phát
          triển của toàn xã hội. Thế nhưng, sách cũng có nhiều loại, bên cạnh
          những cuốn sách hữu ích, có giá trị thì cũng có những cuốn sách không
          mang lại lợi ích gì cho con người. Khi chúng ta đọc được những cuốn
          sách hay thì đồng nghĩa với việc chúng ta sẽ nạp thêm được một lượng
          tri thức có ích nhất định, từ đó có thể phát triển và hoàn thiện bản
          thân. Ngược lại, nếu chúng ta không biết chọn lựa sách hay, sách tốt,
          đọc phải những cuốn sách có nội dung vô bổ, tiêu cực, xấu xa thì sẽ
          gây ra những hậu quả nghiêm trọng, đặc biệt là ở lứa tuổi thiếu niên,
          thanh thiếu niên. Những cuốn sách đó không chỉ tạo ra hình ảnh xấu cho
          xã hội mà còn gián tiếp gây ra những tệ nạn như hiếp dâm, trộm cắp, ma
          túy, giết người, phản động,… từ đó làm ảnh hưởng đến sự phát triển của
          đất nước, thế giới. Chính vì vậy, khi đọc sách, mỗi chúng ta cần phải
          tỉnh táo, cẩn thận lựa chọn sách hay, sách tốt để đọc, đó chính là một
          trong những biểu hiện đẹp của văn hóa đọc sách.
        </p>
      </div>
    );
  }
}

//mapStateToProps: map state của redux vào react (Props), redux có thể lấy các biến qua câu lệnh this.props

const mapStateToProps = (state) => {
  return {
    isLoggedIn: state.user.isLoggedIn,
    language: state.app.language,
  };
};

//fire events của redux
const mapDispatchToProps = (dispatch) => {
  return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(HomeFooter);
//connect:kết nối react với redux
//

/**
 * muốn lưu thông tin vào redux thì phải fire actions của nó
 * muốn lấy thông tin thì dùng hàm map và lấy qua biến props
 */
