import React, { Component } from "react";
import "./ViewMap.scss";
import { Map, TileLayer, Marker, Popup, Tooltip } from "react-leaflet";
import L from "leaflet";
import osm from "./osm-provider";
import "leaflet/dist/leaflet.css";
import markerImage1 from "../../../assets/images/marker.png";
import markerImage2 from "../../../assets/images/marker_icon.png";
import cities from "./cities.json";
import { connect } from "react-redux";
import * as actions from "../../../store/actions";
import { getAllHotpots } from "../../../services/hotpotService";
import { getAllHotpotNameServices } from "../../../services/hotpotService";
import HomeHeader from "../../HomePage/HomeHeader";
import SearchBox from "./SearchBox";
const markerIcon1 = new L.Icon({
  iconUrl: markerImage1,
  iconSize: [40, 40],
  iconAnchor: [17, 46],
  popupAnchor: [0, -46],
});

const markerIcon2 = new L.Icon({
  iconUrl: markerImage2,
  iconSize: [25, 35],
  iconAnchor: [17, 46],
  popupAnchor: [0, -46],
});

class ViewMap extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arrHotpots: [],
      center: { lat: 21.0277644, lng: 105.8341598 },
      ZOOM_LEVEL: 12,
      location: {
        loaded: false,
        coordinates: { lat: "", lng: "" },
        error: null,
      },
      selectPosition: "",
    };
    this.mapRef = React.createRef();
  }

  // componentDidUpdate(prevProps, prevState, snapshot) {
  //   // if (prevProps.topHotpotsRedux !== this.props.topHotpotsRedux) {
  //   //   this.setState({
  //   //     arrHotpots: this.props.topHotpotsRedux,
  //   //   });
  //   // }
  // }

  async componentDidMount() {
    //this.props.loadTopHotpots();
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(this.onSuccess, this.onError);
    } else {
      this.onError({ code: 0, message: "Geolocation not supported" });
    }
    // let res = await getAllHotpotNameServices();
    // if (res && res.errCode === 0) {
    //   this.setState({
    //     arrHotpots: res.data ? res.data : [],
    //   });
    // }
    let res = await getAllHotpotNameServices();
    console.log("RES", res);
    if (res && res.errCode === 0) {
      this.setState({
        arrHotpots: res.data,
      });
    }
  }

  onSuccess = (location) => {
    this.setState({
      location: {
        loaded: true,
        coordinates: {
          lat: location.coords.latitude,
          lng: location.coords.longitude,
        },
        error: null,
      },
      center: {
        lat: location.coords.latitude,
        lng: location.coords.longitude,
      },
    });
  };

  onError = (error) => {
    this.setState({
      location: {
        loaded: true,
        coordinates: { lat: "", lng: "" },
        error: {
          code: error.code,
          message: error.message,
        },
      },
    });
  };

  showMyLocation = () => {
    const { location, ZOOM_LEVEL } = this.state;
    if (location.loaded && !location.error) {
      this.mapRef.current.leafletElement.flyTo(
        [location.coordinates.lat, location.coordinates.lng],
        ZOOM_LEVEL,
        { animate: true }
      );
    } else {
      alert(location.error.message);
    }
  };

  handleViewDetailHotpot = (hotpot) => {
    this.props.history.push(`/detail-hpHotpot/${hotpot.id}`);
  };

  handelSelectPosition = (position) => {
    // Xử lý logic khi nhận được vị trí được chọn từ SearchBox
    console.log("Selected position from SearchBox:", position);
    // Thực hiện các hành động cần thiết với vị trí đã chọn
    // Ví dụ: Cập nhật state, hiển thị thông tin trên bản đồ, v.v.
    this.setState({ selectPosition: position });
  };

  render() {
    let { center, ZOOM_LEVEL, location, arrHotpots, selectPosition } =
      this.state;
    console.log("lat", selectPosition?.lat);
    return (
      <div className="map-container">
        {/* <HomeHeader isShowBanner={false} /> */}
        <div className="search-box">
          <SearchBox
            selectPosition={selectPosition}
            handelSelectPosition={this.handelSelectPosition}
          />
        </div>
        <div className="map-body">
          {/* <div className="btn-locate justify-content-center">
            <button className="btn btn-primary" onClick={this.showMyLocation}>
              Locate Me <i className="fas fa-globe"></i>
            </button>
          </div> */}

          <div className="map text-center">
            <Map center={center} zoom={ZOOM_LEVEL} ref={this.mapRef}>
              <TileLayer
                url={osm.maptiler.url}
                attribution={osm.maptiler.attribution}
              />
              if(!selectPosition)
              {
                {
                  /* {location.loaded && !location.error && (
                <Marker
                  icon={markerIcon1}
                  position={[
                    location.coordinates.lat,
                    location.coordinates.lng,
                  ]}
                ></Marker>
              )} */
                }
              }
              else
              {
                <Marker
                  icon={markerIcon1}
                  position={[selectPosition?.lat, selectPosition?.lon]}
                ></Marker>
              }
              {arrHotpots.map((item, idex) => {
                let imageBase64 = "";
                let name = `${item.name}`;
                if (item.image) {
                  imageBase64 = new Buffer(item.image, "base64").toString(
                    "binary"
                  );
                }
                return (
                  <Marker
                    //position={[10.7285492637418, 106.616285747586]}
                    position={[item.latitude, item.longitude]}
                    icon={markerIcon2}
                    key={idex}
                    riseOnHover={true}
                    riseOffset={200}
                    //onClick={() => this.handleViewDetailHotpot(item)}
                  >
                    <Tooltip
                      className="tool-tip"
                      direction="bottom"
                      offset={[0, -10]}
                      opacity={1}
                    >
                      <div className="customize-border">
                        <div className="image">
                          <div
                            className="bg-image section-hotpot"
                            style={{
                              backgroundImage: `url(${imageBase64})`,
                            }}
                          />
                        </div>
                        <div className="info">
                          <div className="name">{name}</div>
                          <div className="address">{item.note}</div>
                        </div>
                      </div>
                    </Tooltip>
                    {/* <Popup open={true}>
                    <b className="detail-hotpot">
                      {item.name}, {item.address}
                    </b>
                  </Popup> */}
                  </Marker>
                );
              })}
            </Map>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    isLoggedIn: state.user.isLoggedIn,
    language: state.app.language,
    //topHotpotsRedux: state.admin.topHotpots,
  };
};

//fire events của redux
const mapDispatchToProps = (dispatch) => {
  return {
    //loadTopHotpots: () => dispatch(actions.fetchTopHotpot()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ViewMap);
