import React, { Component } from "react";
import { connect } from "react-redux";
import { FormattedMessage } from "react-intl";
import "./DetailRestaurant.scss";
import HomeHeader from "../../HomePage/HomeHeader";
import HotpotSchedule from "../Hotpot/HotpotSchedule";
import HotpotExtraInfo from "../Hotpot/HotpotExtraInfo";
import HotpotProfile from "../Hotpot/HotpotProfile";
import {
  getDetailRestaurantById,
  getAllCodeService,
  getDetailHotpotById,
} from "../../../services/hotpotService";
import Slider from "react-slick";
import _ from "lodash";
import { LANGUAGES } from "../../../utils";
class DetailHpHotpot extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arrHpId: [],
      dataDetail: {},
    };
  }

  async componentDidMount() {
    if (
      this.props.match &&
      this.props.match.params &&
      this.props.match.params.id
    ) {
      let id = this.props.match.params.id;

      let res = await getDetailHotpotById({
        id: id,
      });
      if (res && res.errCode === 0) {
        let data = res.data;
        let arrHpId = [];
        if (data && !_.isEmpty(data)) {
          let arr = data.hpHotpot;
          if (arr && arr.length > 0) {
            arr.map((item) => {
              arrHpId.push(item.id);
            });
          }
        }

        this.setState({
          dataDetail: res.data,
          arrHotpotId: arrHpId,
        });
      }
    }
  }

  async componentDidUpdate(prevProps, prevState, snapshot) {}

  handleViewDetailhpHotpot = (item) => {
    //this.props.history.push(`/detail-hp/${item.id}`);
    console.log("view detail hp");
  };

  render() {
    let { arrHpId, dataDetail } = this.state;
    console.log("Check dataDetailType arrHotpotId", arrHpId);
    let { language } = this.props;
    return (
      <div className="detail-type-container">
        <HomeHeader />
        <div className="detail-type-body">
          <div className="description-type">
            {dataDetail && !_.isEmpty(dataDetail) && (
              <>
                <div className="name">{dataDetail.name}</div>

                <div
                  dangerouslySetInnerHTML={{
                    __html: dataDetail.descriptionHTML,
                  }}
                ></div>
              </>
            )}
          </div>

          {arrHpId &&
            arrHpId.length > 0 &&
            arrHpId.map((item, index) => {
              return (
                <div className="each-hotpot" key={index}>
                  <div className="section-body">
                    <Slider {...this.props.settings}>
                      {dataDetail &&
                        dataDetail.length > 0 &&
                        dataDetail.map((item, index) => {
                          let imageBase64 = "";
                          if (item.image) {
                            imageBase64 = new Buffer(
                              item.image,
                              "base64"
                            ).toString("binary");
                          }
                          return (
                            <div
                              className="section-customize restaurant-child"
                              key={index}
                              onClick={() =>
                                this.handleViewDetailhpHotpot(item)
                              }
                            >
                              <div
                                className="bg-image section-type"
                                style={{
                                  backgroundImage: `url(${imageBase64})`,
                                }}
                              />
                              <div className="hpHotpot-name">{item.name}</div>
                            </div>
                          );
                        })}
                    </Slider>
                  </div>
                  {/* <div className="dt-content-left">
                    <div className="profile-hotpot">
                      <HotpotProfile
                        hotpotId={item}
                        isShowDescriptionHotpot={true}
                        isShowLinkDetail={true}
                        isShowPrice={false}
                      />
                    </div>
                  </div>
                  <div className="dt-content-right">
                    <div className="hotpot-schedule">
                      <HotpotSchedule hotpotIdFromParent={item} />
                    </div>
                    <div className="hotpot-extra-info">
                      <HotpotExtraInfo hotpotIdFromParent={item} />
                    </div>
                  </div> */}
                </div>
              );
            })}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    language: state.app.language,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(DetailHpHotpot);
