import React, { Component } from "react";
import "./ViewMap.scss";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import osm from "./osm-provider";
import "leaflet/dist/leaflet.css";
import markerImage1 from "../../../assets/images/marker.png";
import markerImage2 from "../../../assets/images/marker_icon.png";
import cities from "./cities.json";

const markerIcon1 = new L.Icon({
  iconUrl: markerImage1,
  iconSize: [40, 40],
  iconAnchor: [17, 46],
  popupAnchor: [0, -46],
});

const markerIcon2 = new L.Icon({
  iconUrl: markerImage2,
  iconSize: [25, 35],
  iconAnchor: [17, 46],
  popupAnchor: [0, -46],
});

class ViewMap extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arrHotpots: [],
      center: { lat: 21.0277644, lng: 105.8341598 },
      ZOOM_LEVEL: 12,
      location: {
        loaded: false,
        coordinates: { lat: "", lng: "" },
        error: null,
      },
    };
    this.mapRef = React.createRef();
  }

  componentDidMount() {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(this.onSuccess, this.onError);
    } else {
      this.onError({ code: 0, message: "Geolocation not supported" });
    }
  }

  onSuccess = (location) => {
    this.setState({
      location: {
        loaded: true,
        coordinates: {
          lat: location.coords.latitude,
          lng: location.coords.longitude,
        },
        error: null,
      },
      center: {
        lat: location.coords.latitude,
        lng: location.coords.longitude,
      },
    });
  };

  onError = (error) => {
    this.setState({
      location: {
        loaded: true,
        coordinates: { lat: "", lng: "" },
        error: {
          code: error.code,
          message: error.message,
        },
      },
    });
  };

  showMyLocation = () => {
    const { location, ZOOM_LEVEL } = this.state;
    if (location.loaded && !location.error) {
      this.mapRef.current.leafletElement.flyTo(
        [location.coordinates.lat, location.coordinates.lng],
        ZOOM_LEVEL,
        { animate: true }
      );
    } else {
      alert(location.error.message);
    }
  };

  render() {
    const { center, ZOOM_LEVEL, location } = this.state;
    return (
      <>
        <div id="map" className="row">
          <div className="col text-center">
            <h2>React-leaflet - Bản đồ cơ bản OpenStreetMap</h2>
            <p>Đang tải bản đồ cơ bản sử dụng lớp từ maptiler</p>

            <div className="col d-flex justify-content-center">
              <button className="btn btn-primary" onClick={this.showMyLocation}>
                Locate Me <i className="fas fa-globe"></i>
              </button>
            </div>

            <div className="col">
              <Map center={center} zoom={ZOOM_LEVEL} ref={this.mapRef}>
                <TileLayer
                  url={osm.maptiler.url}
                  attribution={osm.maptiler.attribution}
                />
                {location.loaded && !location.error && (
                  <Marker
                    icon={markerIcon1}
                    position={[
                      location.coordinates.lat,
                      location.coordinates.lng,
                    ]}
                  ></Marker>
                )}

                {cities.map((city, idx) => (
                  <Marker
                    position={[city.lat, city.lng]}
                    icon={markerIcon2}
                    key={idx}
                  >
                    <Popup>
                      <b>
                        {city.city}, {city.country}
                      </b>
                    </Popup>
                  </Marker>
                ))}
              </Map>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default ViewMap;
