import React, { Component } from "react";
import "./Hotpot.scss";
import { connect } from "react-redux";
import { FormattedMessage } from "react-intl";
import Slider from "react-slick";
import * as actions from "../../../store/actions";
import { LANGUAGES } from "../../../utils";
import { withRouter } from "react-router";
import {
  getHotpotByLocation,
  getAllCodeService,
} from "../../../services/hotpotService";
import _ from "lodash";
class Hotpot extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arrHotpotId: [],
      arrHotpots: [],
      listProvince: [],
      searchValue: "",
      currentPage: 1,
    };
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    if (prevProps.topHotpotsRedux !== this.props.topHotpotsRedux) {
      this.setState({
        arrHotpots: this.props.topHotpotsRedux,
      });
    }
  }

  async componentDidMount() {
    this.props.loadTopHotpots();
    let res = await getHotpotByLocation("ALL");

    let resProvince = await getAllCodeService("PROVINCE");
    if (res && res.errCode === 0 && resProvince && resProvince.errCode === 0) {
      let data = res.data;
      let arrHotpotId = [];
      if (data && !_.isEmpty(data)) {
        let arr = data.hotpotType;
        if (arr && arr.length > 0) {
          arr.map((item) => {
            arrHotpotId.push(item.id);
          });
        }
      }

      let dataProvince = resProvince.data;
      console.log("Check resProvince", resProvince);
      if (dataProvince && dataProvince.length > 0) {
        dataProvince.unshift({
          createAt: null,
          keyMap: "ALL",
          type: "PROVINCE",
          valueEn: "All",
          valueVi: "Toàn quốc",
        });
      }

      this.setState({
        arrHotpotId: arrHotpotId,
        listProvince: dataProvince ? dataProvince : [],
      });
    } else {
      console.log("LOI");
    }
  }

  handleViewDetailHotpot = (hotpot) => {
    this.props.history.push(`/detail-hotpot/${hotpot.id}`);
  };

  handleOnChangeSelect = async (event) => {
    let location = event.target.value;

    let res = await getHotpotByLocation(location);
    console.log("res:::", res);
    if (res && res.errCode === 0) {
      let data = res.data;
      let arrHotpots = [];
      arrHotpots = data;
      this.setState({
        arrHotpots: arrHotpots,
      });
    } else {
      console.log("LOOOIIII");
    }
  };

  handleOnChangeSearch = (event) => {
    const searchValue = event.target.value;
    this.setState({ searchValue });
  };

  handlePageChange = (page) => {
    this.setState({ currentPage: page });
  };

  render() {
    const { arrHotpots, currentPage } = this.state;
    const { settings } = this.props;
    const startIndex = (currentPage - 1) * 8;
    const endIndex = Math.min(startIndex + 8, arrHotpots.length);
    const visibleHotpots = arrHotpots.slice(startIndex, endIndex);

    return (
      <div className="body">
        <div className="hotpot-search">
          <div className="container">
            <div className="row">
              <div className="col-md-2 search-location">
                <select onChange={(event) => this.handleOnChangeSelect(event)}>
                  {listProvince &&
                    listProvince.length > 0 &&
                    listProvince.map((item, index) => {
                      return (
                        <option key={index} value={item.keyMap}>
                          {language === LANGUAGES.VI
                            ? item.valueVi
                            : item.valueEn}
                        </option>
                      );
                    })}
                </select>
              </div>
              <div className="col-md-8 search-name">
                <input
                  type="text"
                  placeholder="Search by name"
                  onChange={this.handleOnChangeSearch}
                />
              </div>
            </div>
          </div>
        </div>

        <div className="section-share section-hotpot">
          <div className="section-container">
            <div className="section-header">
              <span className="title-section">
                <FormattedMessage id="homepage.outstanding-hotpot" />
              </span>
              <button className="btn-section">
                <FormattedMessage id="homepage.more-info" />
              </button>
            </div>
            <div className="section-body">
              <Slider {...settings}>
                {visibleHotpots.map((item, index) => {
                  let imageBase64 = "";
                  let name = `${item.name}`;
                  if (item.image) {
                    imageBase64 = new Buffer(item.image, "base64").toString(
                      "binary"
                    );
                  }
                  return (
                    <div
                      className="section-customize"
                      key={index}
                      onClick={() => this.handleViewDetailHotpot(item)}
                    >
                      <div className="customize-border">
                        <div className="outer-bg">
                          <div
                            className="bg-image section-hotpot"
                            style={{
                              backgroundImage: `url(${imageBase64})`,
                            }}
                          />
                        </div>
                        <div className="position text-center">
                          <div>{name}</div>
                          <div>Lẩu buffet</div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </Slider>
            </div>
            <div className="pagination">
              {Array.from(
                { length: Math.ceil(arrHotpots.length / 8) },
                (_, i) => (
                  <button key={i} onClick={() => this.handlePageChange(i + 1)}>
                    {i + 1}
                  </button>
                )
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    isLoggedIn: state.user.isLoggedIn,
    language: state.app.language,
    topHotpotsRedux: state.admin.topHotpots,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    loadTopHotpots: () => dispatch(actions.fetchTopHotpot()),
  };
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Hotpot));
