import React, { Component } from "react";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import osm from "./osm-provider";
import "leaflet/dist/leaflet.css";
import markerImage1 from "../../../assets/images/marker.png";
import markerImage2 from "../../../assets/images/marker_icon.png";
import cities from "./cities.json";

const markerIcon1 = new L.Icon({
  iconUrl: markerImage1,
  iconSize: [40, 40],
  iconAnchor: [17, 46],
  popupAnchor: [0, -46],
});

const markerIcon2 = new L.Icon({
  iconUrl: markerImage2,
  iconSize: [25, 35],
  iconAnchor: [17, 46],
  popupAnchor: [0, -46],
});

class ViewMap extends Component {
  constructor(props) {
    super(props);
    this.state = {
      center: { lat: 0, lng: 0 }, // Khởi tạo center với giá trị mặc định
    };
  }

  componentDidMount() {
    // Lấy vị trí hiện tại của người dùng và cập nhật state
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(this.onSuccess, this.onError);
    } else {
      alert("Geolocation is not supported by this browser.");
    }
  }

  onSuccess = (position) => {
    const { latitude, longitude } = position.coords;
    this.setState({ center: { lat: latitude, lng: longitude } });
  };

  onError = (error) => {
    alert("Error occurred while getting your location: " + error.message);
  };

  render() {
    const { center } = this.state;
    const ZOOM_LEVEL = 12;

    return (
      <div id="map" className="row">
        <div className="col text-center">
          <h2>React-leaflet - Bản đồ cơ bản OpenStreetMap</h2>
          <p>Đang tải bản đồ cơ bản sử dụng lớp từ maptiler</p>
          <div className="col">
            <Map center={center} zoom={ZOOM_LEVEL}>
              <TileLayer
                url={osm.maptiler.url}
                attribution={osm.maptiler.attribution}
              />
              <Marker
                icon={markerIcon1}
                position={[center.lat, center.lng]}
              ></Marker>

              {cities.map((city, idx) => (
                <Marker
                  position={[city.lat, city.lng]}
                  icon={markerIcon2}
                  key={idx}
                >
                  <Popup>
                    <b>
                      {city.city}, {city.country}
                    </b>
                  </Popup>
                </Marker>
              ))}
            </Map>
          </div>
        </div>
      </div>
    );
  }
}

export default ViewMap;
