import React, { useRef, useState } from "react";
import "./ViewMap.scss";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import osm from "./osm-provider";
import "leaflet/dist/leaflet.css";
import markerImage from "../../../assets/images/marker.png";
import cities from "./cities.json";
import useGeoLocation from "./useGeoLocation";

const markerIcon = new L.Icon({
  iconUrl: markerImage,
  iconSize: [40, 40],
  iconAnchor: [17, 46], //[left/right, top/bottom]
  popupAnchor: [0, -46], //[left/right, top/bottom]
});

const ViewMap = () => {
  const [center, setCenter] = useState({ lat: 13.084622, lng: 80.248357 });
  const ZOOM_LEVEL = 9;
  const mapRef = useRef();
  const location = useGeoLocation();
  const showMyLocation = () => {
    if (location.loaded && !location.error) {
      mapRef.current.leafletElement.flyTo(
        [location.coordinates.lat, location.coordinates.lng],
        ZOOM_LEVEL,
        { animate: true }
      );
    } else {
      alert(location.error.message);
    }
  };

  return (
    <>
      <div id="map" className="row">
        <div className="col text-center">
          <h2>React-leaflet - Bản đồ cơ bản OpenStreetMap</h2>
          <p>Đang tải bản đồ cơ bản sử dụng lớp từ maptiler</p>
          <div className="col">
            <Map center={center} zoom={ZOOM_LEVEL} ref={mapRef}>
              <TileLayer
                url={osm.maptiler.url}
                attribution={osm.maptiler.attribution}
              />
              {location.loaded && !location.error && (
                <Marker
                  icon={markerIcon}
                  position={[
                    location.coordinates.lat,
                    location.coordinates.lng,
                  ]}
                ></Marker>
              )}

              {/* {cities.map((city, idx) => (
              <Marker
                position={[city.lat, city.lng]}
                icon={markerIcon}
                key={idx}
              >
                <Popup>
                  <b>
                    {city.city}, {city.country}
                  </b>
                </Popup>
              </Marker>
            ))} */}
            </Map>
          </div>
        </div>
      </div>
      <div className="row my-4">
        <div className="col d-flex justify-content-center">
          <button className="btn btn-primary" onClick={showMyLocation}>
            Locate Me <i className="fas fa-trash"></i>
          </button>
        </div>
      </div>
    </>
  );
};

export default ViewMap;
