import React, { Component } from "react";
import { connect } from "react-redux";
import { Redirect, Route, Switch } from "react-router-dom";
import "./ManageSchedule.scss";
import { FormattedMessage } from "react-intl";
import Select from "react-select";
import * as actions from "../../../store/actions";

class ManageSchedule extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  componentDidMount() {
    this.props.fetchAllHotpotNames();
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    if (prevProps.allHotpotNames !== this.props.allHotpotNames) {
      let dataSelect = this.buildDataInputSelect(this.props.allHotpotNames);
      console.log("Type of dataSelect:", typeof dataSelect);
      this.setState({
        listHotpotNames: dataSelect,
      });
      console.log("Type of listHotpotNames:", typeof listHotpotNames);
    }
  }

  render() {
    return (
      <div className="manage-schedule-container">
        <div className="manage-schedule-title">
          <FormattedMessage id="manage-schedule.title" />
        </div>
        <div className="container">
          <div className="row">
            <div className="col-6 form-group">
              <label>Chọn món lẩu</label>
              <input className="form-control" />
            </div>
            <div className="col-6 form-group">
              <label>Chọn ngày</label>
              <input className="form-control" />
            </div>
            <div className="col-12 pick-hour-container"></div>
            <button className="btn btn-primary">Lưu thông tin</button>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    isLoggedIn: state.user.isLoggedIn,
    allHotpotNames: state.admin.allHotpotNames,
    language: state.app.language,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchAllHotpotNames: () => dispatch(actions.fetchAllHotpotNames()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ManageSchedule);
