import React, { Component } from "react";
import { connect } from "react-redux";
import { FormattedMessage } from "react-intl";
import "./HotpotProfile.scss";
import { getExtraInfoHotpotById } from "../../../services/hotpotService";

class HotpotProfile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dataProfile: {},
    };
  }

  async componentDidMount() {
    let data = this.getInfoHotpot(this.props.hotpotId);
    this.setState({
      dataProfile: data,
    });
  }

  async componentDidUpdate(prevProps, prevState, snapshot) {
    if (this.props.hotpotId !== prevProps.hotpotId) {
      // this.getInfoHotpot(this.props.hotpotId)
    }
  }

  getInfoHotpot = async (id) => {
    let result = {};
    if (id) {
      let res = await getExtraInfoHotpotById(id);
      if (res && res.errCode === 0) {
        result = res.data;
      }
    }
    return result;
  };

  render() {
    console.log("check state profile", this.state);
    return <div>Hotpot profile</div>;
  }
}

const mapStateToProps = (state) => {
  return {
    language: state.app.language,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(HotpotProfile);
