import React, { Component } from "react";
import { FormattedMessage } from "react-intl";
import { connect } from "react-redux";
import "./ManageHotpot.scss";
import * as actions from "../../../store/actions";

import MarkdownIt from "markdown-it";
import MdEditor from "react-markdown-editor-lite";
import "react-markdown-editor-lite/lib/index.css";
import Select from "react-select";

const options = [
  { value: "chocolate", label: "Chocolate" },
  { value: "chocolate1", label: "Chocolate1" },
  { value: "chocolate2", label: "Chocolate2" },
];

const mdParser = new MarkdownIt(/* Markdown-it options */);

class ManageHotpot extends Component {
  state = {};
  //constructor:
  //khi component được render thì nó sẽ check hàm constructor đầu tiên
  // khởi tạo những state (những biến mà ta muốn dùng class ManageHotpot này)
  constructor(props) {
    super(props);
    this.state = {
      contentMarkdown: "",
      contentHTML: "",
      selectedHotpot: "",
      description: "",
      listHotpotNames: [],
    };
  }

  componentDidMount() {
    this.props.fetchAllHotpotNames();
  }

  buildDataInputSelect = (data) => {};

  componentDidUpdate(prevProps, prevState, snapshot) {
    if (prevProps.allHotpotNames !== this.props.allHotpotNames) {
      this.setState({
        listHotpotNames: this.props.allHotpotNames,
      });
    }
  }

  handleEditorChange = ({ html, text }) => {
    this.setState({
      contentMarkdown: text,
      contentHTML: html,
    });
  };

  handleSaveContentMarkdown = () => {
    console.log("check state", this.state);
  };

  handleChange = (selectedHotpot) => {
    this.setState({ selectedHotpot });
  };

  handleOnChangeDesc = (event) => {
    this.setState({
      description: event.target.value,
    });
  };

  render() {
    console.log("linh check state", this.state);
    return (
      <div className="manage-hotpot-container">
        <div className="manage-hotpot-title">Tao them thong tin hotpot</div>
        <div className="more-info">
          <div className="content-left form-group">
            <label>Chon lau</label>
            <Select
              value={this.state.selectedHotpot}
              onChange={this.handleChange}
              options={options}
            />
          </div>
          <div className="content-right">
            <label>Thong tin gioi thieu</label>
            <textarea
              className="form-control"
              rows="4"
              onChange={(event) => this.handleOnChangeDesc(event)}
              value={this.state.description}
            >
              asdfghj
            </textarea>
          </div>
        </div>
        <div className="manage-hotpot-editor">
          <MdEditor
            style={{ height: "500px" }}
            renderHTML={(text) => mdParser.render(text)}
            onChange={this.handleEditorChange}
          />
        </div>
        <button
          className="save-content-hotpot"
          onClick={() => this.handleSaveContentMarkdown()}
        >
          Save info
        </button>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    allHotpotNames: state.admin.allHotpotNames,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchAllHotpotNames: () => dispatch(actions.fetchAllHotpotNames()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ManageHotpot);
