import React, { useRef, useState } from "react";
import "./ViewMap.scss";
import { Map, MapContainer, TileLayer } from "react-leaflet";
import osm from "./osm-provider";

const ViewMap = () => {
  const [center, setCenter] = useState({ lat: 13.084622, lng: 80.248357 });
  const ZOOM_LEVEL = 9;
  const mapRef = useRef();

  return (
    <div className="row">
      <div className="col text-center">
        <h2>React-leaflet - Bản đồ cơ bản OpenStreetMap</h2>
        <p>Đang tải bản đồ cơ bản sử dụng lớp từ maptiler</p>
        <div className="map">
          <MapContainer
            center={center}
            zoom={10}
            style={{ width: "100vw", height: "100vh" }}
          >
            <TileLayer
              url="https://api.maptiler.com/maps/basic/256/{z}/{x}/{y}.png?key=YOUR_KEY"
              attribution='<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>'
            />
          </MapContainer>
        </div>
      </div>
    </div>
  );
};

export default ViewMap;
