import axios from "../axios";

const getListCustomerForStaff = (data) => {
  //template string
  return axios.get(
    `/api/get-list-customer-for-staff?staffId=${data.staffId}&date=${data.date}`
  );
};

const getHotpotByStaffId = (staffId) => {
  //template string
  return axios.get(`/api/get-hotpot-by-staffId?staffId=${staffId}`);
};

export { getListCustomerForStaff, getHotpotByStaffId };
