import React, { Component } from "react";
import { connect } from "react-redux";
import "./SearchBox.scss";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import { Button, Divider } from "@material-ui/core";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import markerImage from "../../../assets/images/marker.png";

const NOMINATIM_BASE_URL = "https://nominatim.openstreetmap.org/search?";

const params = {
  q: "",
  format: "json",
  addressdetails: "addressdetails",
};

class SearchBox extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchText: "", // Khởi tạo searchText
      listPlace: [],
    };
  }

  handleInputChange = (event) => {
    this.setState({ searchText: event.target.value }); // Cập nhật searchText khi có thay đổi
  };

  handleListPlace = (event) => {
    this.setState({ listPlace: event.target.value }); // Cập nhật searchText khi có thay đổi
  };

  render() {
    let { searchText, listPlace } = this.state;
    return (
      <div className="search-box-container">
        <div className="up">
          <div className="search-info">
            <OutlinedInput
              value={searchText}
              onChange={this.handleInputChange}
            />
          </div>
          <div className="search-btn">
            <Button
              variant="contained"
              color="primary"
              onClick={() => {
                //s
                const params = {
                  q: searchText,
                  format: "json",
                  addressdetails: 1,
                  polygon_geojson: 0,
                };
                const queryString = new URLSearchParams(params).toString();
                const requestOptions = {
                  method: "GET",
                  redirect: "follow",
                };
                fetch(`${NOMINATIM_BASE_URL}${queryString}`, requestOptions)
                  .then((response) => response.text())
                  .then((result) => {
                    console.log(JSON.parse(result));
                    this.handleListPlace(JSON.parse(result));
                  })
                  .catch((err) => console.log("err:", err));
              }}
            >
              Search
            </Button>
          </div>
        </div>

        <div className="search-list">
          <List component="nav" aria-label="main mailbox folders">
            {listPlace.map((item) => {
              return (
                <div key={item?.osm_id}>
                  <ListItem button>
                    <ListItemIcon>
                      <img
                        className="img"
                        src={markerImage}
                        alt="Placeholder"
                      />
                    </ListItemIcon>
                    <ListItemText primary={item?.display_name} />
                  </ListItem>
                  <Divider />
                </div>
              );
            })}
          </List>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    language: state.app.language,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(SearchBox);
