import React, { Component } from "react";
import { connect } from "react-redux";
import { FormattedMessage } from "react-intl";
import "./BookingModal.scss";
import { Modal } from "reactstrap";
import HotpotProfile from "../HotpotProfile";
import _ from "lodash";

class BookingModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullName: "",
      phoneNumber: "",
      email: "",
      note: "",
      hotpotId: "",
    };
  }

  async componentDidMount() {}

  async componentDidUpdate(prevProps, prevState, snapshot) {
    if (this.props.dataTime !== prevProps.dataTime) {
      if (this.props.dataTime && !_.isEmpty(this.props.dataTime)) {
        let hotpotId = this.props.dataTime.hotpotId;
        this.setState({
          hotpotId: hotpotId,
        });
      }
    }
  }

  handleOnChangeInput = (event, id) => {
    let valueInput = event.target.value;
    let stateCopy = { ...this.state };
    stateCopy[id] = valueInput;
    this.setState({
      ...stateCopy,
    });
  };

  render() {
    let { isOpenModal, closeBookingModal, dataTime } = this.props;
    let hotpotId = dataTime && !_.isEmpty(dataTime) ? dataTime.hotpotId : "";
    console.log("check state inside booking modal", this.state);
    return (
      <Modal
        isOpen={isOpenModal}
        className={"booking-modal-container"}
        size="lg"
        centered
        // backdrop={true}
      >
        <div className="booking-modal-content">
          <div className="booking-modal-header">
            <span className="left">Thông tin đặt lịch khám bệnh</span>
            <span className="right" onClick={closeBookingModal}>
              {/* <i className="fa-solid fa-x"></i> */}
              <i class="fas fa-times"></i>
            </span>
          </div>
          <div className="booking-modal-body">
            <div className="hopot-info">
              <HotpotProfile
                hotpotId={hotpotId}
                isShowDescriptionHotpot={false}
                dataTime={dataTime}
              />
            </div>
            <div className="row">
              <div className="col-12 form-group">
                <label>Ho ten</label>
                <input
                  className="form-control"
                  value={this.setState.fullName}
                  onChange={(event) =>
                    this.handleOnChangeInput(event, "fullName")
                  }
                ></input>
              </div>
              <div className="col-12 form-group">
                <label>So dien thoai</label>
                <input
                  className="form-control"
                  value={this.setState.phoneNumber}
                  onChange={(event) =>
                    this.handleOnChangeInput(event, "phoneNumber")
                  }
                ></input>
              </div>
              <div className="col-12 form-group">
                <label>Email</label>
                <input
                  className="form-control"
                  value={this.setState.email}
                  onChange={(event) => this.handleOnChangeInput(event, "email")}
                ></input>
              </div>
              <div className="col-12 form-group">
                <label>Note</label>
                <input
                  className="form-control"
                  value={this.setState.note}
                  onChange={(event) => this.handleOnChangeInput(event, "note")}
                ></input>
              </div>
            </div>
          </div>
          <div className="booking-modal-footer">
            <button className="btn-booking-confirm" onClick={closeBookingModal}>
              Xac nhan
            </button>
            <button className="btn-booking-cancel" onClick={closeBookingModal}>
              Huy
            </button>
          </div>
        </div>
      </Modal>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    language: state.app.language,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(BookingModal);
