import React, { Component } from "react";
import { connect } from "react-redux";
import "./HotpotSchedule.scss";
import Select from "react-select";
class HotpotSchedule extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  async componentDidMount() {}

  componentDidUpdate(prevProps, prevState, snapshot) {}

  render() {
    let options = [
      { label: "Monday", value: "2" },
      { label: "Tuesday", value: "3" },
      { label: "Wednesday", value: "4" },
    ];
    const customStyles = {
      control: (provided) => ({
        ...provided,
        width: "180px",
        border: "none",
        borderRadius: "none",
        borderBottom: "1.6px solid black",
        boxShadow: "none",
        fontWeight: "600",
        textTransform: "capitalize",
      }),
      menu: (provided) => ({
        ...provided,
        width: "180px",
      }),
    };
    return (
      <div className="hotpot-schedule-container">
        <div className="all-schedule">
          <Select styles={customStyles} options={options} />
        </div>
        <div className="all-available-time"></div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    language: state.app.language,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(HotpotSchedule);
