import React, { Component } from "react";
import { connect } from "react-redux";
import "./HotpotExtraInfo.scss";
import Select from "react-select";
import moment from "moment";
import localization from "moment/locale/vi";
import { LANGUAGES } from "../../../utils";
import {
  getExtraInfoHotpotById,
  getDetailInfoHotpot,
} from "../../../services/hotpotService";
import { FormattedMessage } from "react-intl";
import NumberFormat from "react-number-format";

class HotpotExtraInfo extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isShowDetailInfo: false,
      extraInfo: {},
    };
  }

  async componentDidMount() {}

  async componentDidUpdate(prevProps, prevState, snapshot) {
    if (this.props.hotpotIdFromParent !== prevProps.hotpotIdFromParent) {
      let res = await getExtraInfoHotpotById(this.props.hotpotIdFromParent);
      if (res && res.errCode === 0) {
        this.setState({
          extraInfo: res.data,
        });
      }
    }
  }

  showHideDetailInfo = (status) => {
    this.setState({
      isShowDetailInfo: status,
    });
  };

  render() {
    let { isShowDetailInfo, extraInfo } = this.state;
    console.log("check state ---", this.state);
    return (
      <div className="hotpot-extra-info-container">
        <div className="content-up">
          <div className="text-address"> dia chi nha hang</div>
          <div className="name-restaurant">
            {extraInfo && extraInfo.Restaurant ? extraInfo.Restaurant.name : ""}
          </div>
          <div className="address-restaurant">
            {extraInfo && extraInfo.Restaurant
              ? extraInfo.Restaurant.address
              : ""}
          </div>
        </div>
        <div className="content-down">
          <NumberFormat
            value={234}
            displayType={"text"}
            thousandSeparator={true}
            suffix={"$"}
          />
          {isShowDetailInfo === false && (
            <div className="short-info">
              GIA MON LAU:{" "}
              {extraInfo && extraInfo.priceData
                ? extraInfo.priceData.valueVi
                : ""}{" "}
              <span onClick={() => this.showHideDetailInfo(true)}>
                Xem chi tiet
              </span>
            </div>
          )}
          {isShowDetailInfo === true && (
            <>
              <div className="title-price">GIA MON MAU</div>
              <div className="detail-info">
                <div className="price">
                  <span className="left">GIA MON LAU</span>
                  <span className="right">
                    {" "}
                    {extraInfo && extraInfo.priceData
                      ? extraInfo.priceData.valueVi
                      : ""}
                  </span>
                </div>
                <div className="note">Free voi tre em duoi 1m</div>
              </div>

              <div className="payment">Thanh toan bang the or tien mat</div>
              <div className="hide-price">
                <span onClick={() => this.showHideDetailInfo(false)}>
                  An bang gia
                </span>
              </div>
            </>
          )}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    language: state.app.language,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(HotpotExtraInfo);
