import React, { Component } from "react";

import { connect } from "react-redux";
import { FormattedMessage } from "react-intl";
import Slider from "react-slick";
import * as actions from "../../../store/actions";

class Hotpot extends Component {
  constructor(props) {}

  componentDidMount() {
    this.props.loadTopHotpots();
  }
  render() {
    console.log("check topHotpotsRedux", this.props.topHotpotsRedux);
    return (
      <div className="section-share section-hotpot">
        <div className="section-container">
          <div className="section-header">
            <span className="title-section">Lẩu hot tuần qua</span>
            <button className="btn-section">Xem thêm</button>
          </div>
          <div className="section-body">
            <Slider {...this.props.settings}>
              <div className="section-customize">
                <div className="customize-border">
                  <div className="outer-bg">
                    <div className="bg-image section-hotpot" />
                  </div>
                  <div className="position text-center">
                    <div>Lẩu 1</div>
                    <div>Lẩu buffet</div>
                  </div>
                </div>
              </div>
              <div className="section-customize">
                <div className="customize-border">
                  <div className="outer-bg">
                    <div className="bg-image section-hotpot" />
                  </div>
                  <div className="position text-center">
                    <div>Lẩu 2</div>
                    <div>Lẩu buffet</div>
                  </div>
                </div>
              </div>
              <div className="section-customize">
                <div className="customize-border">
                  <div className="outer-bg">
                    <div className="bg-image section-hotpot" />
                  </div>
                  <div className="position text-center">
                    <div>Lẩu 3</div>
                    <div>Lẩu buffet</div>
                  </div>
                </div>
              </div>
              <div className="section-customize">
                <div className="customize-border">
                  <div className="outer-bg">
                    <div className="bg-image section-hotpot" />
                  </div>
                  <div className="position text-center">
                    <div>Lẩu 4</div>
                    <div>Lẩu buffet</div>
                  </div>
                </div>
              </div>
              <div className="section-customize">
                <div className="customize-border">
                  <div className="outer-bg">
                    <div className="bg-image section-hotpot" />
                  </div>
                  <div className="position text-center">
                    <div>Lẩu 5</div>
                    <div>Lẩu buffet</div>
                  </div>
                </div>
              </div>
              <div className="section-customize">
                <div className="customize-border">
                  <div className="outer-bg">
                    <div className="bg-image section-hotpot" />
                  </div>
                  <div className="position text-center">
                    <div>Lẩu 6</div>
                    <div>Lẩu buffet</div>
                  </div>
                </div>
              </div>
            </Slider>
          </div>
        </div>
      </div>
    );
  }
}

//mapStateToProps: map state của redux vào react (Props), redux có thể lấy các biến qua câu lệnh this.props

const mapStateToProps = (state) => {
  return {
    isLoggedIn: state.user.isLoggedIn,
    language: state.app.language,
    topHotpotsRedux: state.admin.topHotpots,
  };
};

//fire events của redux
const mapDispatchToProps = (dispatch) => {
  return {
    loadTopHotpots: () => dispatch(actions.fetchTopHotpot()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Hotpot);
//connect:kết nối react với redux
//

