import React, { Component } from "react";
import { FormattedMessage } from "react-intl";
import { connect } from "react-redux";
import "./HotpotManage.scss";
import { getAllHotpots } from "../../services/hotpotService";
class HotpotManage extends Component {
  state = {};
  //constructor:
  //khi component được render thì nó sẽ check hàm constructor đầu tiên
  // khởi tạo những state (những biến mà ta muốn dùng class HotpotManage này)
  constructor(props) {
    super(props);
    this.state = {
      arrHotpots: [],
    };
  }

  async componentDidMount() {
    let response = await getAllHotpots("ALL");
    if (response && response.errCode === 0) {
      this.setState(
        {
          arrHotpots: response.hotpots,
        },
        () => {
          console.log("check state hotpot", this.state.arrHotpots);
        }
      );
      console.log("check state hotpot 1", this.state.arrHotpots);
    }
    console.log("get hotpit from node.js:", response);
  }

  /**
   *Life cycle:
   Run component:
   1. Run constructor -> init state (khởi tạo những biến sẽ dùng)
   2. Run Did mount: khi nào muốn gán gtri cho 1 state nào đấy sẽ dùng trong hàm did mount 
   - gọi API lấy giá trị vào và set state cho component 
   - state lưu giá trị của các biến, và được dùng trong render 
   3. Run render 
   render cho ta nhìn thấy trên màn hình 
   *
   *
   *
   *
   */

  render() {
    return (
      <div className="hotpots-container">
        <div className="title">Manage hotpot</div>
        <div className="hotpots-table mt-3 mx-1">
          <table id="customers">
            <tr>
              <th>Name</th>
              <th>ProvinceId</th>
              <th>Restaurant Address</th>
              <th>Phone number</th>
              <th>Restaurant Name</th>
              <th>Restaurant Id</th>
              <th>Type Id</th>
              <th>PriceId</th>
              <th>Note</th>
              <th>PaymentId</th>
              <th>Image</th>
              <th>Count</th>
              <th>Action</th>
            </tr>
            <tr>
              <td>Alfreds Futterkiste</td>
              <td>Maria Anders</td>
              <td>Germany</td>
            </tr>
            <tr>
              <td>Berglunds snabbköp</td>
              <td>Christina Berglund</td>
              <td>Sweden</td>
            </tr>
            <tr>
              <td>Centro comercial Moctezuma</td>
              <td>Francisco Chang</td>
              <td>Mexico</td>
            </tr>
            <tr>
              <td>Ernst Handel</td>
              <td>Roland Mendel</td>
              <td>Austria</td>
            </tr>
            <tr>
              <td>Island Trading</td>
              <td>Helen Bennett</td>
              <td>UK</td>
            </tr>
            <tr>
              <td>Königlich Essen</td>
              <td>Philip Cramer</td>
              <td>Germany</td>
            </tr>
            <tr>
              <td>Laughing Bacchus Winecellars</td>
              <td>Yoshi Tannamuri</td>
              <td>Canada</td>
            </tr>
            <tr>
              <td>Magazzini Alimentari Riuniti</td>
              <td>Giovanni Rovelli</td>
              <td>Italy</td>
            </tr>
            <tr>
              <td>North/South</td>
              <td>Simon Crowther</td>
              <td>UK</td>
            </tr>
            <tr>
              <td>Paris spécialités</td>
              <td>Marie Bertrand</td>
              <td>France</td>
            </tr>
          </table>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {};
};

const mapDispatchToProps = (dispatch) => {
  return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(HotpotManage);
