import React, { Component } from "react";
import { connect } from "react-redux";
import { Redirect, Route, Switch } from "react-router-dom";
import "./ManageSchedule.scss";
import { FormattedMessage } from "react-intl";
import Select from "react-select";
import * as actions from "../../../store/actions";
import { CRUD_ACTIONS, LANGUAGES } from "../../../utils";
import DatePicker from "../../../components/Input/DatePicker";

class ManageSchedule extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listHotpotNames: [],
      selectedHotpot: {},
    };
  }

  componentDidMount() {
    this.props.fetchAllHotpotNames();
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    if (prevProps.allHotpotNames !== this.props.allHotpotNames) {
      let dataSelect = this.buildDataInputSelect(this.props.allHotpotNames);
      console.log("Type of dataSelect:", typeof dataSelect);
      this.setState({
        listHotpotNames: dataSelect,
      });
      console.log("Type of listHotpotNames:", typeof listHotpotNames);
    }
  }

  buildDataInputSelect = (inputData) => {
    let result = [];
    if (inputData && inputData.length > 0) {
      console.log("Type of inputData:", typeof inputData);
      inputData.map((item, index) => {
        let object = {};
        object.label = `${item.name}`;
        object.value = item.id;
        result.push(object);
      });
    }
    return result;
  };

  handleChangeSelect = async (selectedHotpot) => {
    this.setState({ selectedHotpot });
  };

  render() {
    console.log("check state schedule", this.state);
    return (
      <div className="manage-schedule-container">
        <div className="manage-schedule-title">
          <FormattedMessage id="manage-schedule.title" />
        </div>
        <div className="container">
          <div className="row">
            <div className="col-6 form-group">
              <label>Chọn món lẩu</label>
              <Select
                value={this.state.selectedHotpot}
                onChange={this.handleChangeSelect}
                options={this.state.listHotpotNames}
              />
            </div>
            <div className="col-6 form-group">
              <label>Chọn ngày</label>
              <input className="form-control" />
            </div>
            <div className="col-12 pick-hour-container"></div>
            <button className="btn btn-primary">Lưu thông tin</button>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    isLoggedIn: state.user.isLoggedIn,
    allHotpotNames: state.admin.allHotpotNames,
    language: state.app.language,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchAllHotpotNames: () => dispatch(actions.fetchAllHotpotNames()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ManageSchedule);
